import React, { useState } from 'react';
import {w3cwebsocket} from "websocket";

const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleRegister = () => {
        // Tạo một đối tượng websocket
        const ws = new w3cwebsocket('ws://140.238.54.136:8080/chat/chat');
        // Hàm sử lý sự kiện gửi yêu cầu đăng ký
        ws.onopen = () =>{
            // Tạo dữ liệu yêu cầu đăng ký
            const requestData = {
                action: "onchat",
                data: {
                    event: "REGISTER",
                    data: {
                        user: username,
                        pass: password,
                    },
                },
            };

            // Gửi yêu cầu đăng ký thông qua websocket
            ws.send(JSON.stringify(requestData));
        };

        // Đăng ký sự kiện nhận dữ liệu từ server websocket
        ws.onmessage = (event) =>{
            // Xử lý dữ liệu nhận được từ server
            const responeData = JSON.parse(event.data);
            console.log(responeData); // In ra dữ liệu nhận được từ server
        };

        // 












        // Gửi yêu cầu kiểm tra tài khoản đến API
        const checkAccountData = {
            action: 'check_account',
            username,
        };

        // Giả sử API trả về kết quả kiểm tra
        const response = {
            accountExists: true,
        };

        // Xử lý kết quả từ API
        if (response.accountExists) {
            setErrorMessage('Tài khoản đã tồn tại.');
            return;
        }

        // Gửi yêu cầu tạo tài khoản đến API
        const createAccountData = {
            action: 'register',
            username,
            password,
        };

        // Giả sử API trả về kết quả tạo tài khoản
        const createAccountResponse = {
            success: true,
            message: 'Tài khoản đã được tạo thành công.',
        };

        // Xử lý kết quả từ API
        if (createAccountResponse.success) {
            setSuccessMessage(createAccountResponse.message);
            // Hoặc bạn có thể đặt lại giá trị của username và password tại đây:
            // setUsername('');
            // setPassword('');
        } else {
            // Xử lý trường hợp không thành công (nếu cần)
        }

        ws.send(JSON.stringify(createAccountData));
    };

    return (
        <div>
            <h1>Create Account</h1>
            {successMessage && <p>{successMessage}</p>}
            {errorMessage && <p>{errorMessage}</p>}
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleRegister}>Create Account</button>
        </div>
    );
};

export default Register;
